/**
 * Get a random joke from the curated list, avoiding seen jokes
 */
export function getRandomJoke(
  category: JokeCategory = "all",
  seenJokeIds: string[] = []
): Joke {
  // Dad jokes come from API, not static list
  if (category === "dad-jokes") {
    // Return a placeholder - actual dad jokes fetched async
    return {
      id: "dad-loading",
      category: "dad-jokes" as Exclude<JokeCategory, "all">,
      setup: "Loading dad joke...",
      punchline: "",
    };
  }

  const allJokes = category === "all"
    ? CURATED_JOKES
    : CURATED_JOKES.filter((j) => j.category === category);

  // Filter out seen jokes
  const unseenJokes = allJokes.filter((j) => !seenJokeIds.includes(j.id));

  // If all jokes seen, reset and use all jokes
  const jokes = unseenJokes.length > 0 ? unseenJokes : allJokes;

  const randomIndex = Math.floor(Math.random() * jokes.length);
  return jokes[randomIndex];
}

/**
 * Fetch a dad joke from icanhazdadjoke.com API
 */
export async function fetchDadJoke(): Promise<Joke> {
  try {
    const res = await fetch("https://icanhazdadjoke.com/", {
      headers: { Accept: "application/json" },
    });
    const data = await res.json();
    return {
      id: `dad-${data.id}`,
      category: "dad-jokes" as Exclude<JokeCategory, "all">,
      setup: data.joke,
      punchline: "", // Dad jokes are single-line
    };
  } catch {
    // Fallback if API fails
    return {
      id: "dad-fallback",
      category: "dad-jokes" as Exclude<JokeCategory, "all">,
      setup: "Why don't scientists trust atoms? Because they make up everything!",
      punchline: "",
    };
  }
}

/**
 * Get all jokes for a category
 */
export function getJokesByCategory(category: JokeCategory): Joke[] {
  if (category === "all") return CURATED_JOKES;
  if (category === "dad-jokes") return []; // Dad jokes are API-only
  return CURATED_JOKES.filter((j) => j.category === category);
}
